import React, { Component } from "react";
import { Link } from "react-router-dom";
import SearchField from "react-search-field";

interface State {
  searchString: "";
  region: "";
  contries: any[];
}

export default class Home extends Component<State> {
  state: State = {
    searchString: "",
    region: "",
    contries: [],
  };
  componentDidMount() {
    fetch("https://restcountries.eu/rest/v2/all")
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          contries: [] = result,
        });
      });
  }
  handleChange = () => {
    this.setState({
      searchString: (document.getElementById('searchId') as HTMLInputElement).value
    });
  };
  searchRegion = () => {
    this.setState({
        searchString: (document.getElementById('searchRegion') as HTMLInputElement).value
      });
  }
  render() {
    let contries_list = this.state.contries;
    let search = this.state.searchString.trim().toLowerCase();
    let region = this.state.region.trim().toLowerCase();
    if (search.length > 0) {
      contries_list = contries_list.filter(function (user) {
        return user.name.toLowerCase().match(search);
      });
    }
    if (region.length > 0) {
        contries_list = contries_list.filter(function (user) {
          return user.region.toLowerCase().match(region);
        });
    }
    return (
      <div className="container">
        <div className="row filters">
          <input
            type="text"
            value={this.state.searchString}
            id="searchId"
            onChange={this.handleChange}
            placeholder="Search with country name"
          />
          <select name="country" id="searchRegion" className="pull-right" onChange={this.searchRegion}>
            {this.state.contries.map((e, key) => {
                return <option key={key} value={e.value}>{e.region}</option>;
            })}
        </select>
        </div>
        <div className="row">
          {contries_list.map((contry: any) => {
            return (
              <div className="row">
                <Link to={`/Detail/${contry.cioc}`}>
                  <div className="column card">
                    <img src={contry.flag} />
                    <div className="description">
                      <h2 className="name">{contry.name}</h2>
                      <span className="properties">
                        Population : {contry.population}
                      </span>
                      <span className="properties">
                        Region : {contry.region}
                      </span>
                      <span className="properties">
                        Capital : {contry.capital}
                      </span>
                    </div>
                  </div>
                </Link>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
