import React, { Component } from "react";
import { Link } from "react-router-dom";
import SearchField from 'react-search-field';

interface State {
  searchString: "";
  contries: [];
}

export default class Home extends Component<State, any> {
    constructor(props: State){
        super(props);
        this.setState({
            searchString: "",
            contries: [] 
        })
        this.handleChange = this.handleChange.bind(this)
    }
  componentDidMount() {
    fetch("https://restcountries.eu/rest/v2/all")
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          contries: [] = result,
        });
      });
  }
  handleChange = (e) => {
    let currentList=[];
    let newList=[];
    if(e.target.value!=="")
    {
      currentList=this.props.contries
      newList=currentList.filter(item => {
        const lc=item.toLowerCase();
        const filter=e.target.value.toLowerCase();
        return lc.includes(filter)
      })
    } else
    {
      newList=this.props.items
    }
    this.setState({
      filtered: newList
    })

  }
  render() {
    var libraries : [] = this.state.contries,
    searchString = this.state.searchString.trim().toLowerCase();
    if (searchString.length > 0) {
    libraries = libraries.filter(function(i) {
        return i.name.toLowerCase().match( searchString );
    });
    }
    return (
      <div className="container">
        <div className="row filters">
        <input type="text" value={this.state.searchString} onChange={this.handleChange} placeholder="Type here..."/>
        </div>
        <div className="row">
          {this.state.contries.map((contry: any) => {
            return (
              <div className="row">
                <Link to={`/Detail/${contry.cioc}`}>
                  <div className="column card">
                    <img src={contry.flag} />
                    <div className="description">
                      <h2 className="name">{contry.name}</h2>
                      <span className="properties">
                        Population : {contry.population}
                      </span>
                      <span className="properties">
                        Region : {contry.region}
                      </span>
                      <span className="properties">
                        Capital : {contry.capital}
                      </span>
                    </div>
                  </div>
                </Link>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
