import React, { Component } from "react";
import { Link } from "react-router-dom";

interface State {
  searchString: "";
  contries: [];
}

export default class Home extends Component<State> {
    state : State = {
        searchString: "",
        contries: []
    }
  componentDidMount() {
    fetch("https://restcountries.eu/rest/v2/all")
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          contries: [] = result,
        });
      });
  }
  render() {
    const [searchTerm, setSearchTerm] = React.useState("");
    const [searchResults, setSearchResults] = React.useState([]);
    const handleChange = e => {
      setSearchTerm(e.target.value);
    };
    React.useEffect(() => {
      const results = this.state.contries.filter(name =>
        name.toLowerCase().includes(searchTerm)
      );
      setSearchResults(results);
    }, [searchTerm]);
    return (
      <div className="container">
        <div className="row filters">
        <input type="text" className="form-control form-control-lg" placeholder="Search" onChange={this.filterList}/>
        </div>
        <div className="row">
          {this.state.contries.map((contry: any) => {
            return (
              <div className="row">
                <Link to={`/Detail/${contry.cioc}`}>
                  <div className="column card">
                    <img src={contry.flag} />
                    <div className="description">
                      <h2 className="name">{contry.name}</h2>
                      <span className="properties">
                        Population : {contry.population}
                      </span>
                      <span className="properties">
                        Region : {contry.region}
                      </span>
                      <span className="properties">
                        Capital : {contry.capital}
                      </span>
                    </div>
                  </div>
                </Link>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
