import React, { Component } from "react";
import { Link } from "react-router-dom";
import SearchField from "react-search-field";

interface State {
  searchString: "";
  contries: any[];
}

export default class Home extends Component<State> {
  state: State = {
    searchString: "",
    contries: [],
  };
  componentDidMount() {
    fetch("https://restcountries.eu/rest/v2/all")
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          contries: [] = result,
        });
      });
  }
  handleChange = () => {
    this.setState({
      searchString: (document.getElementById('searchId') as HTMLInputElement).value
    });
  };
  render() {
    let contries_list = this.state.contries;
    let search = this.state.searchString.trim().toLowerCase();
    if (search.length > 0) {
      contries_list = contries_list.filter(function (user) {
        return user.name.toLowerCase().match(search);
      });
    }
    return (
      <div className="container">
        <div className="row filters">
          <input
            type="text"
            value={this.state.searchString}
            id="searchId"
            ref="search"
            onKeyUp={this.handleChange}
            placeholder="Search with country name"
          />
        </div>
        <div className="row">
          {this.state.contries.map((contry: any) => {
            return (
              <div className="row">
                <Link to={`/Detail/${contry.cioc}`}>
                  <div className="column card">
                    <img src={contry.flag} />
                    <div className="description">
                      <h2 className="name">{contry.name}</h2>
                      <span className="properties">
                        Population : {contry.population}
                      </span>
                      <span className="properties">
                        Region : {contry.region}
                      </span>
                      <span className="properties">
                        Capital : {contry.capital}
                      </span>
                    </div>
                  </div>
                </Link>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
