import React, { Component } from "react";
import { Link } from "react-router-dom";

interface State {
  searchString: "";
  contries: [];
}

export default class Home extends Component<State> {
        state : State = {
            searchString: "",
            contries: []
        }
  componentDidMount() {
    fetch("https://restcountries.eu/rest/v2/all")
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          contries: result,
        });
      });
  }
  handleChange = event => {
    this.setState({ searchString: event.target.value });
  };
  render() {
    const { searchString, contries } = this.state;
    const lowercasedFilter = searchString.toLowerCase();
    const filteredData = contries.filter(item => {
      return Object.keys(item).some(key =>
        item[key].toLowerCase().includes(lowercasedFilter)
      );
    });
    return (
      <div className="container">
        <div className="row filters">
          <input
            type="text"
            defaultValue={this.state.searchString}
            ref="search"
            onChange={this.handleChange}
            placeholder="Search with country name"
          />
        </div>
        <div className="row">
          {this.state.contries.map((contry: any) => {
            return (
              <div className="row">
                <Link to={`/Detail/${contry.cioc}`}>
                  <div className="column card">
                    <img src={contry.flag} />
                    <div className="description">
                      <h2 className="name">{contry.name}</h2>
                      <span className="properties">
                        Population : {contry.population}
                      </span>
                      <span className="properties">
                        Region : {contry.region}
                      </span>
                      <span className="properties">
                        Capital : {contry.capital}
                      </span>
                    </div>
                  </div>
                </Link>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
