import React, { Component } from "react";
import { Link } from "react-router-dom";
import SearchField from "react-search-field";

interface State {
  searchString: "";
  contries: any[];
}

export default class Home extends Component<State> {
  state: State = {
    searchString: "",
    contries: [],
  };
  componentDidMount() {
    fetch("https://restcountries.eu/rest/v2/all")
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          contries: [] = result,
        });
      });
  }
  handleChange = (e) => {
    var currentList : string[] = []
    let newList = [];
    if (e.target.value !== "") {
      currentList = this.state.contries;
      newList = currentList.filter((item) => {
        const lc = item.toLowerCase();
        const filter = e.target.value.toLowerCase();
        return lc.includes(filter);
      });
    } else {
      newList = this.props.contries;
    }
    this.setState({
      contries: newList,
    });
  };
  render() {
    return (
      <div className="container">
        <div className="row filters">
          <input
            type="text"
            className="input"
            placeholder="Search..."
            onChange={this.handleChange}
          />
        </div>
        <div className="row">
          {this.state.contries.map((contry: any) => {
            return (
              <div className="row">
                <Link to={`/Detail/${contry.cioc}`}>
                  <div className="column card">
                    <img src={contry.flag} />
                    <div className="description">
                      <h2 className="name">{contry.name}</h2>
                      <span className="properties">
                        Population : {contry.population}
                      </span>
                      <span className="properties">
                        Region : {contry.region}
                      </span>
                      <span className="properties">
                        Capital : {contry.capital}
                      </span>
                    </div>
                  </div>
                </Link>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
